import Box from '@mui/material/Box';
import OutlinedInput from '@mui/material/OutlinedInput';
import InputLabel from '@mui/material/InputLabel';
import InputAdornment from '@mui/material/InputAdornment';
import FormControl from '@mui/material/FormControl';
import { useState } from 'react';

function LogTransaction() {

    const [ amountInput, setAmountInput ] = useState("");
    const [ amountInputError, setAmountInputError ] = useState(false);

    function isNumeric(str) {
        return /^[0-9]+$/.test(str);
      }

    const checkAmountInput = (event) => {
        setAmountInputError(isNumeric(event.target.value));
        setAmountInput(event.target.value)
    }

    return (
        <Box sx={{border: "1px solid red", display: "flex", justifyContent: "center", width: "100%", height: "100%"}}>
            <Box sx={{border: "1px solid blue", display: "flex", justifyContent: "center", alignItems: "center", width: 500, height: 500}}>
            <FormControl sx={{ m: 1 }}>
                <InputLabel htmlFor="outlined-adornment-amount">Amount</InputLabel>
                <OutlinedInput
                    id="outlined-adornment-amount"
                    startAdornment={<InputAdornment position="start">$</InputAdornment>}
                    label="Amount"
                    onChange={(event)=> {checkAmountInput(event)}}
                    value={amountInput}
                    error={amountInputError}
                />
            </FormControl>
            </Box>
        </Box>
    )
}

export default LogTransaction;
